import React, { useState } from 'react'
import "./Res.css";
import Header from './Header';
import Menu from './Menu';
import Items from './Items';
import Footer from './Footer';
import menu_items from './Fdata'

const App = () => {
  const [list, setlist] = useState(menu_items);
  const catList =()=>{
          return(
           setlist((curelem)=>{
            return  curelem.category === menu_items.category;
           })
          )
  }
 const selectall =()=>{
     return(
      setlist(menu_items)
     )
 }
  return (
    <>
       <Header/>
       <Menu  catList={catList} selectall={selectall}/>
       <Items list={list}/>
       <Footer/>
    </>
  )
}

export default App
